import React, { useState, useEffect } from 'react';
import './App.css';
// import { db, auth, functions } from './firebaseConfig'; // You'll need to set up firebase
// import { doc, onSnapshot, updateDoc } from "firebase/firestore";
// import { httpsCallable } from "firebase/functions";

const statusColors = {
  Safe: '#34D399',
  Busy: '#FBBF24',
  Offline: '#6B7280',
  Help: '#F87171'
};

function App() {
  const [bubble, setBubble] = useState(null);
  const [loading, setLoading] = useState(true);
  const [myNode, setMyNode] = useState(null);
  // const user = auth.currentUser;

  useEffect(() => {
    // Mock data, replace with your firebase logic
    const mockBubble = {
      id: 'bubble1',
      name: "Family Bubble",
      members: [
        { id: 'm1', name: 'Mom', status: 'Safe' },
        { id: 'm2', name: 'Dad', status: 'Busy' },
        { id: 'm3', name: 'Me', status: 'Safe' }
      ]
    };
    setBubble(mockBubble);
    setMyNode({ id: 'm3', name: 'Me', status: 'Safe' });
    setLoading(false);

    /*
    if (user) {
      const nodeRef = doc(db, "nodes", user.uid);
      const unsubscribe = onSnapshot(nodeRef, (doc) => {
        if (doc.exists()) {
          const nodeData = { id: doc.id, ...doc.data() };
          setMyNode(nodeData);

          // Now we need to get the bubble data. This is a bit tricky
          // as we need to find the bubble this node belongs to.
          // For now, we'll just show the user's own status.
          setBubble({
            name: "My Bubble",
            members: [nodeData] // simplified view
          });
        }
        setLoading(false);
      });
      return () => unsubscribe();
    }
    */
  }, [/* user */]);

  const handleStatusChange = async (status) => {
    /*
    if (user) {
      const updateStatus = httpsCallable(functions, 'updateStatus');
      await updateStatus({ status });
    }
    */
   // Mock update
   setMyNode({...myNode, status});
   const updatedMembers = bubble.members.map(m => m.id === myNode.id ? {...m, status} : m);
   setBubble({...bubble, members: updatedMembers});
  };

  const handleUpgrade = () => {
    /*
    const upgradeToOwner = httpsCallable(functions, 'upgradeToOwner');
    upgradeToOwner().then(() => {
      alert('You have been upgraded to a bubble owner!');
    });
    */
   alert("Upgrade functionality not implemented in mock version.");
  };

  if (loading) {
    return <div className="App-header">Loading...</div>;
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>{bubble ? bubble.name : "Welcome"}</h1>
        <div className="bubble-view">
          {bubble && bubble.members.map(member => (
            <div key={member.id} className="member-node" style={{ backgroundColor: statusColors[member.status] }}>
              {member.name}
            </div>
          ))}
        </div>
        {myNode && (
          <div className="status-controls">
            <h3>Update Your Status</h3>
            <div className="status-buttons">
              {Object.keys(statusColors).map(status => (
                <button
                  key={status}
                  className="status-button"
                  style={{ backgroundColor: statusColors[status] }}
                  onClick={() => handleStatusChange(status)}
                >
                  {status}
                </button>
              ))}
            </div>
          </div>
        )}
        <button className="upgrade-button" onClick={handleUpgrade}>
          Upgrade to Bubble Owner
        </button>
      </header>
    </div>
  );
}

export default App;
