import React, { useState, useEffect } from 'react';
import { ArrowLeft, ChevronRight, Circle, ShieldCheck, Star, Plus } from 'lucide-react';
import CustomSelect from '../ui/CustomSelect';

// ✅ Correct import
import { Purchases } from '@revenuecat/purchases-js';
import { auth } from '../../firebase';

const REVENUECAT_WEB_API_KEY = "YOUR_PUBLIC_REVENUECAT_WEB_KEY";

const CreateBubbleFlow = ({ onComplete, onBack, isPremium }) => {
  const [step, setStep] = useState(1);
  const [bubbleName, setBubbleName] = useState('');
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [userPhoto, setUserPhoto] = useState(null);
  const [relationshipRole, setRelationshipRole] = useState('');

  const [offerings, setOfferings] = useState(null);
  const [loadingOfferings, setLoadingOfferings] = useState(true);
  const [offeringsError, setOfferingsError] = useState(null);

  // ✅ Initialize RevenueCat once
  useEffect(() => {
    const initRC = async () => {
      if (Purchases.isConfigured) return;

      const user = auth.currentUser;
      const uid = user?.uid;

      try {
        await Purchases.configure({
          apiKey: REVENUECAT_WEB_API_KEY,
          appUserID: uid || undefined,
        });
        console.log("RevenueCat initialized for user:", uid);
      } catch (e) {
        console.error("RevenueCat init error:", e);
      }
    };

    initRC();
  }, []);

  // Fetch offerings when entering the paywall
  useEffect(() => {
    if (step === 6 && Purchases.isConfigured) {
      const fetchOfferings = async () => {
        try {
          setLoadingOfferings(true);
          const result = await Purchases.getOfferings();
          console.log("Offerings:", result);
          setOfferings(result.current);
        } catch (e) {
          console.error("Fetch offerings error:", e);
          setOfferingsError("Failed to load subscription plans.");
        } finally {
          setLoadingOfferings(false);
        }
      };

      fetchOfferings();
    }
  }, [step]);

  // Correct purchase handler
  const handlePurchase = async (pkg) => {
    try {
      const { customerInfo } = await Purchases.purchasePackage(pkg);

      if (customerInfo.entitlements.active.active_bubble) {
        console.log("✔ Subscription active!");
        onComplete();
      } else {
        console.log("Purchase succeeded but entitlement inactive.");
      }
    } catch (e) {
      if (!e.userCancelled) {
        console.error("Purchase failed:", e);
      }
    }
  };

  const nextStep = () => setStep((s) => s + 1);
  const prevStep = () => setStep((s) => s - 1);

  const relationshipOptions = [
    { value: '', label: 'Select your role', disabled: true },
    { value: 'Mom', label: 'Mom' },
    { value: 'Dad', label: 'Dad' },
    { value: 'Brother', label: 'Brother' },
    { value: 'Sister', label: 'Sister' },
    { value: 'Guardian', label: 'Guardian' },
    { value: 'Custom', label: 'Custom' },
  ];

  const renderStep = () => {
    switch (step) {
      case 1:
        return (
          <Step
            title="Name Your Bubble"
            subtitle="This will be the name of your shared family space."
            onNext={nextStep}
            onBack={onBack}
            canGoNext={bubbleName.trim() !== ''}
          >
            <input
              type="text"
              placeholder="e.g., The Miller Family"
              value={bubbleName}
              onChange={(e) => setBubbleName(e.target.value)}
              className="w-full text-center font-bold text-2xl px-4 py-5 bg-gray-900/50 border-2 border-gray-700 rounded-xl text-white"
            />
          </Step>
        );

      case 2:
        return (
          <Step
            title="What's Your Name?"
            subtitle="This is how you'll appear to others in the bubble."
            onNext={nextStep}
            onBack={prevStep}
            canGoNext={firstName && lastName}
          >
            <div className="flex space-x-4">
              <input
                type="text"
                placeholder="First Name"
                value={firstName}
                onChange={(e) => setFirstName(e.target.value)}
                className="w-1/2 text-center font-bold text-xl px-4 py-5 bg-gray-900/50 border-2 border-gray-700 rounded-xl text-white"
              />
              <input
                type="text"
                placeholder="Last Name"
                value={lastName}
                onChange={(e) => setLastName(e.target.value)}
                className="w-1/2 text-center font-bold text-xl px-4 py-5 bg-gray-900/50 border-2 border-gray-700 rounded-xl text-white"
              />
            </div>
          </Step>
        );

      case 3:
        return (
          <Step
            title="Add Your Photo"
            subtitle="This will be visible to members in your bubble."
            onNext={nextStep}
            onBack={prevStep}
            canGoNext={!!userPhoto}
          >
            <input
              type="file"
              accept="image/*"
              id="user-photo-upload"
              hidden
              onChange={(e) => {
                const file = e.target.files[0];
                if (!file) return;

                const reader = new FileReader();
                reader.onload = (ev) => setUserPhoto(ev.target.result);
                reader.readAsDataURL(file);
              }}
            />

            <label
              htmlFor="user-photo-upload"
              className="w-32 h-32 rounded-full bg-gray-800 border-2 border-gray-700 flex items-center justify-center mx-auto cursor-pointer overflow-hidden"
            >
              {userPhoto ? (
                <img src={userPhoto} className="w-full h-full object-cover" />
              ) : (
                <Plus size={48} className="text-gray-500" />
              )}
            </label>
          </Step>
        );

      case 4:
        return (
          <Step
            title="Your Role"
            subtitle="This helps personalize your bubble."
            onNext={nextStep}
            onBack={prevStep}
            canGoNext={relationshipRole.trim() !== ''}
          >
            <CustomSelect
              options={relationshipOptions}
              value={relationshipRole}
              onChange={setRelationshipRole}
            />
          </Step>
        );

      case 5:
        return (
          <Step
            title="Ready to Create Your Bubble!"
            subtitle="One more step to activate your bubble."
            onNext={isPremium ? onComplete : nextStep}
            onBack={prevStep}
            canGoNext={true}
          >
            <p className="text-center text-gray-300">
              {firstName} {lastName} • {relationshipRole}
            </p>
          </Step>
        );

      case 6:
        return (
          <Paywall
            offerings={offerings}
            loading={loadingOfferings}
            error={offeringsError}
            onBack={prevStep}
            onSubscribe={handlePurchase}
          />
        );

      default:
        return null;
    }
  };

  return <div className="min-h-screen p-4 text-white">{renderStep()}</div>;
};



/* ---------------- PAYWALL ---------------- */

const Paywall = ({ offerings, loading, error, onBack, onSubscribe }) => {
  const [selectedPackage, setSelectedPackage] = useState(null);

  useEffect(() => {
    if (offerings?.availablePackages?.length > 0) {
      setSelectedPackage(offerings.availablePackages[0]);
    }
  }, [offerings]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p className="text-red-400">{error}</p>;

  const packages = offerings?.availablePackages || [];

  return (
    <div>
      <button onClick={onBack} className="mb-4 text-gray-400">Back</button>

      <h1 className="text-3xl font-bold mb-4">Unlock Your Bubble</h1>

      {packages.map((pkg) => (
        <div
          key={pkg.identifier}
          className={`p-4 border rounded-xl mb-4 cursor-pointer ${
            selectedPackage?.identifier === pkg.identifier ? "border-purple-500" : "border-gray-700"
          }`}
          onClick={() => setSelectedPackage(pkg)}
        >
          <h2 className="text-xl font-bold">{pkg.product.title}</h2>
          <p className="text-2xl font-bold">{pkg.product.priceString}</p>
        </div>
      ))}

      <button
        onClick={() => onSubscribe(selectedPackage)}
        className="w-full py-4 bg-gradient-to-r from-purple-600 to-blue-600 rounded-xl"
      >
        Subscribe & Create Bubble
      </button>
    </div>
  );
};

export default CreateBubbleFlow;
