import React, { useState, useEffect } from 'react';
import { auth } from './firebase';
import MainApp from './components/bubble/MainApp';
import Welcome from './components/auth/Welcome';
import Login from './components/auth/Login';
import JoinBubble from './components/auth/JoinBubble';

export default function FamilyBubbleApp() {
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [view, setView] = useState('welcome'); // welcome, login, join, main
  const [joinToken, setJoinToken] = useState(null);

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged(user => {
      setCurrentUser(user);
      setLoading(false);
      if (user) {
        setView('main');
      }
    });
    return () => unsubscribe();
  }, []);

  const handleLogout = () => {
    auth.signOut().then(() => {
      setView('welcome');
      setJoinToken(null);
    });
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-gray-950 via-gray-900 to-blue-950 flex items-center justify-center">
        <div className="w-16 h-16 border-4 border-t-transparent border-blue-500 rounded-full animate-spin"></div>
      </div>
    );
  }

  if (!currentUser) {
    if (view === 'welcome') {
      return <Welcome onLogin={() => { setView('login'); setJoinToken(null); }} onJoin={() => setView('join')} />;
    }
    if (view === 'join') {
      return <JoinBubble onJoin={(token) => { setJoinToken(token); setView('login'); }} onBack={() => setView('welcome')} />;
    }
    if (view === 'login') {
      return <Login onLoginSuccess={() => setView('main')} />;
    }
  }

  return (
    <div className="min-h-screen bg-gray-950">
      <MainApp userId={currentUser.uid} onLogout={handleLogout} joinToken={joinToken} />
    </div>
  );
}
